$.fn.moveIt=function(){var t=$(window),s=[];$(this).each(function(){s.push(new moveItItem($(this)))}),window.addEventListener("scroll",function(){var e=t.scrollTop();s.forEach(function(t){t.update(e)})},{passive:!0})};var moveItItem=function(t){this.el=$(t),this.speed=parseInt(this.el.attr("data-scroll-speed"))};moveItItem.prototype.update=function(t){this.el.css("transform","translateX("+-(t/this.speed)+"px)")},$(function(){$("[data-scroll-speed]").moveIt()});