function unwrapTariffcard(){console.log("Скрипт разворачивания карточки загружен"),document.querySelectorAll(".tariff-card__toggle-button").forEach(t=>{t.addEventListener("click",t=>{"basic-tariff-detailed"===t.target.id?basicTariffcard=document.querySelector("#basic-tariff").classList.toggle("tariff-card__toggle-button--pressed"):"standart-tariff-detailed"===t.target.id&&(basicTariffcard=document.querySelector("#standart-tariff").classList.toggle("tariff-card__toggle-button--pressed"))})})}unwrapTariffcard();